    ngOnInit() {
      this.loadSurahs();
      this.loadFullQuran();
    }
    loadSurahs() {
      this.surahs = this.surahData.map((data, index) => ({
        chapter: index + 1,
        name: data.name,
        juz: data.juz,
        verses: data.verses,
        revelationPlace: data.revelationPlace,
        category: data.category
      }));
      this.filteredSurahs = [...this.surahs];
      this.loading = false;
    }
    filterSurahs() {
      if (this.showSearchMode) {
        this.performContentSearch();
      } else {
        this.filteredSurahs = this.surahs.filter(surah => {
          const matchesSearch = !this.searchQuery || surah.name.toLowerCase().includes(this.searchQuery.toLowerCase()) || surah.chapter.toString().includes(this.searchQuery);
          const matchesRevelationPlace = !this.selectedRevelationPlace || surah.revelationPlace === this.selectedRevelationPlace;
          const matchesCategory = !this.selectedCategory || surah.category === this.selectedCategory;
          const matchesJuz = !this.selectedJuz || surah.juz === +this.selectedJuz;
          return matchesSearch && matchesRevelationPlace && matchesCategory && matchesJuz;
        });
      }
    }
    clearFilters() {
      this.searchQuery = '';
      this.filterSurahs();
    }
    clearAllFilters() {
      this.searchQuery = '';
      this.selectedRevelationPlace = '';
      this.selectedCategory = '';
      this.selectedJuz = '';
      this.filterSurahs();
    }
    switchMode(mode) {
      this.currentMode = mode;
      if (mode === 'surah') {
        this.clearFilters();
      }
    }
